import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [clientAuthToken, setClientAuthToken] = useState({});

  useEffect(() => {
    const baseURL = "https://us.battle.net/oauth/token";
    const data = "grant_type=client_credentials";
    const options = {
      auth: {
        username: process.env.REACT_APP_CLIENT_ID,
        password: process.env.REACT_APP_CLIENT_SECRET,
      },
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
    };

    const getClientAuthToken = () => {
      axios.post(baseURL, data, options)
        .then(function(clientAuthToken) {
          setClientAuthToken(clientAuthToken);
        })
        .catch(function(error) {
          console.log(error);
        });
    };
    getClientAuthToken();
  }, []);


  const handleOnClick = () => {
    const baseURL = "https://us.api.blizzard.com/data/wow/";
    const APIroute = "creature-family/index";
    const nameSpace = "?namespace=static-classic-us";
    const locale = "&locale=en_US";

    axios.get(`${baseURL}${APIroute}${nameSpace}${locale}`, {
      headers: {
        Authorization: `Bearer ${clientAuthToken.data.access_token}`,
        // 'Battlenet-Namespace': "static-classic-us",
      }
    })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };




  return (
    <div className="App">
      <button
        onClick={handleOnClick}
      >
        Click Me
      </button>
    </div>
  );
};

export default App;
