import React, { useState, useEffect } from 'react';
import axios from 'axios';
import getResource from './api/resourceCalls';
import './App.css';

const App = () => {
  const [clientAuthToken, setClientAuthToken] = useState({});

  useEffect(() => {
    // const baseURL = "https://us.battle.net/oauth/token";
    // const data = "grant_type=client_credentials";
    // const options = {
    //   auth: {
    //     username: process.env.REACT_APP_CLIENT_ID,
    //     password: process.env.REACT_APP_CLIENT_SECRET,
    //   },
    //   headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
    // };

    const authAxios = axios.create({
      baseURL: "https://us.battle.net/oauth/token",
      headers: {
        Authorization: `Bearer ${clientAuthToken.data.access_token}`
      },
      data: "grant_type=client_credentials",
      auth: {
        username: process.env.REACT_APP_CLIENT_ID,
        password: process.env.REACT_APP_CLIENT_SECRET,
      },
    });




    const getClientAuthToken = () => {
      authAxios.post()
        .then(function(clientAuthToken) {
          setClientAuthToken(clientAuthToken);
        })
        .catch(function(error) {
          console.log(error);
        });
    };
    getClientAuthToken();
  }, []);


  const handleOnClick = () => {
    getResource(clientAuthToken, "item-class/index");
  };




  return (
    <div className="App">
      <button
        onClick={handleOnClick}
      >
        Click Me
      </button>
    </div>
  );
};

export default App;
