/* eslint-disable space-before-function-paren */
import axios from 'axios';
// 22418


const searchQuerybase = "https://us.api.blizzard.com/data/wow/search/";
const document = "item";
const nameSpace = "?namespace=static-classic-us";

const searchQuery = `${searchQuerybase}${document}${nameSpace}&has_queue=true`


const getResource = async (clientAuthToken) => {
  const resourceAxios = axios.create({
    // baseURL: searchQuery,
    // baseURL: `https://us.api.blizzard.com/data/wow/${resourceRoute}?namespace=static-classic-us&locale=en_US`,
    baseURL: `https://us.api.blizzard.com/data/wow/item/22418&locale=en_US`,

    headers: {
      Authorization: `Bearer ${clientAuthToken}`,
      "Battlenet-Namespace": "static-9.0.5_37760-us",
    }
  });

  const response = await resourceAxios.get();
  return response.data;
};

export default getResource;
