/* eslint-disable space-before-function-paren */
import axios from 'axios';

const getResource = async (clientAuthToken) => {
  const resourceAxios = axios.create({
    // baseURL: `https://us.api.blizzard.com/data/wow/${resourceRoute}?namespace=static-classic-us&locale=en_US`,
    baseURL: `https://us.api.blizzard.com/data/wow/item/12640?namespace=static-classic-9.0.5_37760-us`,

    headers: {
      Authorization: `Bearer ${clientAuthToken}`
    }
  });

  const response = await resourceAxios.get();
  return response.data;
};

export default getResource;
