import axios from 'axios';

const getResource = async (clientAuthToken, resourceRoute) => {
  // const baseURL = "https://us.api.blizzard.com/data/wow/";
  // const nameSpace = "?namespace=static-classic-us";
  // const locale = "&locale=en_US";
  const options = {
    // headers: {
    //   Authorization: `Bearer ${clientAuthToken.data.access_token}`
    // }
  };

  const authAxios = axios.create({
    baseURL: `https://us.api.blizzard.com/data/wow/${resourceRoute}?namespace=static-classic-us&locale=en_US`,
    headers: {
      Authorization: `Bearer ${clientAuthToken.data.access_token}`
    }


  })



  axios.get(`${baseURL}${resourceRoute}${nameSpace}${locale}`, options)
    .then(function(payLoad) {
      console.log(payLoad);
    })
    .catch(function(error) {
      console.log(error);
    });
};

export default getResource;
