import axios from 'axios';

const getAuthToken = async (baseUrl) => {
  try {
    return await axios.get(baseUrl);
  } catch (error) {
    if (error.response) {
      throw new Error(`HTTP error! response status: ${error.response.status}`)
    } else if (error.request) {
      throw new Error(`HTTP error! request: ${error.request}`)
    } else {
      throw new Error(`HTTP error! ${error.message}`)
    }
  }
};

export default getAuthToken;

