import React, { useState, useEffect } from 'react';
import axios from 'axios';
import getResource from './api/getResource';
import './App.css';

const App = () => {
  const [clientAuthToken, setClientAuthToken] = useState({});
  const [tempData, setTempData] = useState({});

  useEffect(() => {
    const URL = "https://us.battle.net/oauth/token";
    const data = "grant_type=client_credentials";
    const options = {
      auth: {
        username: process.env.REACT_APP_CLIENT_ID,
        password: process.env.REACT_APP_CLIENT_SECRET,
      },
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
    };

    const getClientAuthToken = () => {
      axios.post(URL, data, options)
        .then(function(clientAuthToken) {
          setClientAuthToken(clientAuthToken);
        })
        .catch(function(error) {
          console.log(error);
        });
    };
    getClientAuthToken();
  }, []);


  const handleOnClick = () => {
    getResource(clientAuthToken, "item-class/index")
      .then(function(response) {
        setTempData(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };




  return (
    <div className="App">
      <button
        onClick={handleOnClick}
      >
        Click Me
      </button>
      <div>
        {tempData}

      </div>
    </div>
  );
};

export default App;
