import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [clientAuthToken, setClientAuthToken] = useState({});

  useEffect(() => {
    /* eslint-disable space-before-function-paren */
    const getClientAuthToken = async () => {
      await axios.post(
        'https://us.battle.net/oauth/token',
        "grant_type=client_credentials",
        {
          auth: {
            username: process.env.REACT_APP_CLIENT_ID,
            password: process.env.REACT_APP_CLIENT_SECRET,
          },
          // data: "grant_type=client_credentials",
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
        })


        // await axios({
        //   auth: {
        //     username: process.env.REACT_APP_CLIENT_ID,
        //     password: process.env.REACT_APP_CLIENT_SECRET,
        //   },
        //   method: 'post',
        //   url: 'https://us.battle.net/oauth/token',
        //   data: "grant_type=client_credentials",
        //   headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
        // })
        .then(function(clientAuthToken) {
          setClientAuthToken(clientAuthToken);
        })
        .catch(function(error) {
          console.log(error);
        });
    };
    getClientAuthToken();
  }, []);


  const handleOnClick = () => {
    const baseURL = "https://us.api.blizzard.com/data/wow/";

    axios.get(`${baseURL}creature-family/index?namespace=static-classic-us&locale=en_US`, {
      headers: {
        Authorization: `Bearer ${clientAuthToken.data.access_token}`,
        // 'Battlenet-Namespace': "static-classic-us",
      }
    })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };




  return (
    <div className="App">
      <button
        onClick={handleOnClick}
      >
        Click Me
      </button>
    </div>
  );
};

export default App;
