export const getToken = () => {
  return axios.get(`https://us.battle.net/oauth/token`, {
    auth: {
      username: BNET_ID,
      password: BNET_SECRET,
    },
    params: {
      grant_type: 'client_credentials',
    }
  }).then(response => response)
}

const token = getToken;
console.log(token)
