import getClientAuthToken from './api/OAuth';
import './App.css';

const App = () => {
  const [token, setToken] = useState({});
  // console.log(token);

  useEffect(() => {
    // const getClientAuthToken = async () => {
    //   await axios({
    //     auth: {
